// --- Database Connection ---
const connectDB = async () => {
    try {
      const conn = await mongoose.connect(process.env.MONGO_URI);
      console.log(`âœ… MongoDB Connected: ${conn.connection.host}`);
    } catch (error) {
      console.error(`âŒ Error connecting to MongoDB: ${error.message}`);
      process.exit(1); 
    }
};

// --- Import Data Function ---
const importData = async () => {
    await connectDB();
    try {
        await User.deleteMany();
        await Product.deleteMany();

        console.log('ğŸ—‘ï¸ Existing data destroyed...');

        const hashedPassword = await bcrypt.hash(adminUser.password, 10);
        await User.create({
            name: adminUser.name,
            email: adminUser.email,
            password: hashedPassword,
        });

        await Product.insertMany(products);

        console.log('âœ¨ Data Imported Successfully!');
        process.exit();

    } catch (error) {
        console.error(`âŒ Error with data import: ${error.message}`);
        process.exit(1);
    }
};

// --- Destroy Data Function ---
const destroyData = async () => {
    await connectDB();
    try {
        await User.deleteMany();
        await Product.deleteMany();

        console.log('ğŸ—‘ï¸ Data Destroyed Successfully!');
        process.exit();
    } catch (error) {
        console.error(`âŒ Error destroying data: ${error.message}`);
        process.exit(1);
    }
};

// --- Execution ---
if (process.argv[2] === '-d') {
    destroyData();
} else {
    importData();
}